bool is_move_valid(int i, int j) {
    pos_t move = {pos.x + i, pos.y + j};
    pos_t target = find_nearest_target(pos, CELL_IN_NEED);

    return is_move_valid_g(pos, move, target, CELL_FIRST_RESP, first_resp_policies[id]);
}

