    target = pos;
    target.x += i;
    target.y += j;
}

void teleport_to_in_need() {
    set_map(pos, CELL_EMPTY);
    pos = target;
    set_map(pos, CELL_FIRST_RESP); // From CELL_ASSISTING back to CELL_FIRST_RESP
}

